                                var japanTime = new Date(now.getTime() + japanTimeOffset);

                                var options = { month: 'long', day: 'numeric' };
                                var formattedDate = new Intl.DateTimeFormat('en-US', options).format(japanTime);

                                var hours = japanTime.getUTCHours().toString().padStart(2, '0');
                                var minutes = japanTime.getUTCMinutes().toString().padStart(2, '0');
                                var seconds = japanTime.getUTCSeconds().toString().padStart(2, '0');

                                return ' Japan Standard Time (JST): ' + formattedDate + ', ' + hours + ':' + minutes + ':' + seconds;
                            }

                            function updateJapanTime() {
                                document.getElementById('japan-time').textContent = getJapanTime();
